/* ==UserStyle==
@name           Dark-Zhihu
@namespace      USO Archive
@author         ST ASC
@description    `目前暂时是<a href="http://ASC8384.top" target="_blank">ASC_8384 </a>基于<a href="https://userstyles.org/styles/138553/dark-zhihu-leptc-mod" target="_blank">Dark zhihu LePtC mod(version:2018.02.20) </a>修改而成，对作者表示感谢。CC0 协议赛高！喜欢的话，请在评论里给好评，顺便去<a href="https://github.com/ASC8384/Dark-Zhihu" target="_blank">GitHub </a>里点个star，谢谢！有不足之处的话，为了便于反馈，请在我的<a href="https://github.com/ASC8384/Dark-Zhihu" target="_blank">GitHub </a> 里提issue，谢谢！`
@version        20200611.14.47
@license        CC0-1.0
@preprocessor   uso
@advanced dropdown hide "隐藏内容（主页）" {
	all "隐藏页脚、按钮*" <<<EOT a.btn-backtotop.btn-action, .CornerButtons, .FeedbackButton-button-3waL {
    display: none !important;
}
.Footer {
    display: none!important;
} EOT;
none "不隐藏" <<<EOT  EOT;
footer "隐藏页脚（右下角的链接）" <<<EOT .Footer {
    display: none!important;
}
 EOT;
button "隐藏按钮（反馈与回到顶部）" <<<EOT a.btn-backtotop.btn-action, .CornerButtons, .FeedbackButton-button-3waL {
    display: none !important;
}
 EOT;

}
@advanced dropdown recommend "隐藏内容（问题）" {
	recommends "隐藏相关推荐*" <<<EOT /*相关推荐*\/
div.Card-header.RelatedCommodities-title,
div.Card-section.RelatedCommodities-list {
    display: none !important;
} EOT;
none "不隐藏" <<<EOT  EOT;

}
@advanced dropdown img "图片明暗变化" {
	pics "开启*" <<<EOT /*图片明暗变化*\/
img {
    opacity: .7 !important;
    transition: opacity .2s
}
img:hover,
a:hover img,
#mpiv-popup {
    opacity: 1 !important
} EOT;
none "关闭" <<<EOT  EOT;

}
@advanced dropdown search "隐藏内容（搜索）" {
	none "显示*" <<<EOT  EOT;
autocomplete "隐藏搜索自动补全" <<<EOT /*搜索自动补全*\/
div.Menu.AutoComplete-menu.SearchBar-menu.SearchBar-noValueMenu,
div.Menu.AutoComplete-menu.SearchBar-menu.SearchBar-hasValueMenu {
    display: none !important;
}
 EOT;

}

==/UserStyle== */
